    return (
      <div>
      <div className="row1">
      <button id="clear" style={{paddingLeft : 60}} onClick={this.handleClick}>AC</button>
      </div>
      <div className="row2">
      <button id="one">1</button>
      <button id="two">2</button>
      <button id="three">3</button>
      <button id="add">+</button>
