import React, { Component } from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return  (
      <div>
      <p>hi</p>
      <InputBar />
      <Buttons />
      </div>
    );
  }
}

class InputBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      input : 0,
      currentValue : 0,
    }
    // if (isNaN(input))
  }
  render() {
    return (
    <input id="display">{this.state.input}</input>
    );
  }

}


class Buttons extends React.Component {
  constructor(props) {
    super(props);
  }

  // do we need to pass in parameters for handle click
  render() {
    return (
      <div>
      <button id="clear" onClick={this.handleClick}>AC</button>
      <button id="add">+</button>
      <button id="one">1</button>
      <button id="two">2</button>
      <button id="three">3</button>
      <button id="subtract">-</button>
      <button id="four">4</button>
      <button id="five">5</button>
      <button id="six">6</button>
      <button id="multiply">*</button>
      <button id="seven">7</button>
      <button id="eight">8</button>
      <button id="nine">9</button>
      <button id="divide">/</button>
      <button id="negate">+/-</button>
      <button id="zero">0</button>
      <button id="decimal">.</button>
      </div>
    );
  }
}
/*
We need - 
Input box that also displays text when the equals button is pressed. 
everything else is just ui. 

we need to keep track of the current number that is displayed.
text/input should probably be its own component, buttons another, and app for everything else. 

ToDo - App.css

Extra features - remainder, fractions, +/-
*/





export default App;
