import React, { Component } from 'react';
import './App.css';

class App extends Component {
   render () {
    return (
      <div>
      <WelcomeMessage />
      <Buttons />
      </div>
    );
  }
}

class WelcomeMessage extends Component {
  render() {
    return (
      <div>
      <p>Welcome to a React Calculator!</p>
      <p>This is possibly the best calculator in the world.</p>
      </div>
    );
  }
}


class Buttons extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      input : '',
      oldValue : 1,
    }

    this.handleClick = this.handleClick.bind(this);
  }

  // but what if the expression starts with an operator -- could do an if statement
  calculateResult() {
    try {
      (eval(this.state.input)) 
    } catch(err) {
      this.setState({input : 0});
      return "Invalid input - please try again";
    }
    return eval(this.state.input);
  }

  handleClick(symbol) {
    if (symbol == 'clear') {
      this.setState({input : ''});
      this.setState({oldValue : 1});
    } else if (symbol == 'equals'){
      //this.setState({input : this.calculateResult});
    } else {
      this.setState({input : this.state.input + ' ' + symbol})
    }
  }

  // apply a regular button class style and a special one for all clear
  render() {
    return (
      <div>
      <InputBar input={this.state.input}/>
      <div className="row1">
      <button id="clear" style={{paddingLeft : 40}} onClick={() => this.handleClick('clear')}>AC</button>
      <button id="add" onClick={() => this.handleClick('+')}>+</button>
      </div>

      <div className="row2">
      <button id="one" onClick={() => this.handleClick('1')}>1</button>
      <button id="two" onClick={() => this.handleClick('2')}>2</button>
      <button id="three" onClick={() => this.handleClick('3')}>3</button>
      <button id="subtract" onClick={() => this.handleClick('-')}>-</button>
      </div>

      <div className="row3">
      <button id="four" onClick={() => this.handleClick('4')}>4</button>
      <button id="five" onClick={() => this.handleClick('5')}>5</button>
      <button id="six" onClick={() => this.handleClick('6')}>6</button>
      <button id="multiply" onClick={() => this.handleClick('*')}>*</button>
      </div>

      <div className="row4">
      <button id="seven" onClick={() => this.handleClick('7')}>7</button>
      <button id="eight" onClick={() => this.handleClick('8')}>8</button>
      <button id="nine" onClick={() => this.handleClick('9')}>9</button>
      <button id="divide" onClick={() => this.handleClick('/')}>/</button>
      </div>

      <div className="row5">
      <button id="negate" onClick={() => this.handleClick('*-1')}>+/-</button>
      <button id="zero" onClick={() => this.handleClick('0')}>0</button>
      <button id="decimal" onClick={() => this.handleClick('.')}>.</button>
      <button id="equals" onClick={() => this.handleClick('=')}>=</button>
      </div>
      </div>
    );
  }
}

class InputBar extends Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
    <div>
    <p id = "display" style = {{paddingLeft : "100px", borderWith:"100", borderColor : "red"}}>{this.props.input}</p>
    </div>
    );
  }
}


// add style stuff to app.css
// do we need to pass in parameters for handle click
// so pass props in which are the current values

// so i think you should have a seperate component for the input bar too. the question is, how to get information
// from the buttons component to the input component. maybe we could render the input inside of buttons?  

// so add everything to a string--- when the equals button is pressedthen you loop through the string, and if it is a number/decimal, you add it to the current number. 
// when it is not a number, parse the number and store the last operand. Then, when you reach either the end of the string or the
// next operand, perform the operator conditionally on which operand. 

// overall flow - 
// so basically all of the logic needs to be done before the input bar. the input bar should just render, because if we're passing stuff down
// app 
//  buttons
//  input bar
//  input bar should'nt logically be a child of the buttons class. 
// i think the logic for pressing button and changing states dependent on that should be done in the button class. 

/*
ToDo - App.css
cannot let user do multiple decimals or operands(so ignore multiple), multiple zeros to start.
Extra features - remainder, fractions, using the keyboard instead of clicking

*/


export default App;
